
public class JavaOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*12+34-->Here,  digits are operands and '+' are Operator
		Unary--> single operands
		Binary-->Two operands
		Ternary--> Three operands
		*/

		//Logical Operators
		System.out.println(!false);
		
		System.out.println("---------------- Equality Operators -------------");
		// Equality operators
		System.out.println(34==78);
		System.out.println(78==78);
		System.out.println(34<=78);
		
		System.out.println("---------------- Conditional Operators -------------");
		System.out.println((34==34) &&(56==56));
		System.out.println((34==54) &&(56==56));
		
		//Or Operator
		//either one condition should be true to ge the answer is true
		System.out.println((123>=124) || (10==11));
		
		
		
	}

}
